import Image from "../nillkin-case-1.jpg";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useDispatch } from "react-redux";


function Product(props) {
  const { product } = props;
  const price = product.price;
  const baseImagePath = "../image-store-tester/uploads/";
  const image = product.productImage.replace(/\\/g, '/').split('/').pop();
  console.log(image);
  const imageSource = `${baseImagePath}${image}`;
  let percentOff;
  let offPrice = `$${product.price}`;
  const dispatch = useDispatch();
  if (props.percentOff && props.percentOff > 0) {
    percentOff = (
      <div
        className="badge bg-dim py-2 text-white position-absolute"
        style={{ top: "0.5rem", right: "0.5rem" }}
      >
        {props.percentOff}% OFF
      </div>
    );

    offPrice = (
      <>
        <del>{"$"}{product.price}</del> {product.price - (props.percentOff * product.price) / 100}
      </>
    );
  }

  return (
    <div className="col">
      <div className="card shadow-sm">
        <Link to="/products/1" href="!#" replace>
          {percentOff}
          <img
            className="card-img-top bg-dark cover"
            height="200"
            alt={product.name}
            src={imageSource}
          />
        </Link>
        <div className="card-body">
          <h5 className="card-title text-center text-dark text-truncate">
          {product.name}
          </h5>
          <p className="card-text text-center text-muted mb-0">{offPrice}</p>
          <div className="d-grid d-block">
            <button className="btn btn-outline-dark mt-3" onClick={() => dispatch({ type: "ADD_ITEM", payload: { id: 1, name: product.name, price: product.price, quantity: 1, image: {Image}} })}>
              <FontAwesomeIcon icon={["fas", "cart-plus"]} /> Add to cart
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Product;
