import React, { useState, useEffect } from 'react';

function SignUp() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8000/users/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message);
        setIsSubmitted(true); // Set isSubmitted to true when the form is submitted
      } else {
        setMessage(data.error || data.message);
      }
    } catch (error) {
      console.error('Error during signup:', error);
      setMessage('An error occurred. Please try again.');
    }
  };

  // Reset the form fields whenever isSubmitted changes
  useEffect(() => {
    if (isSubmitted) {
      setEmail('');
      setPassword('');
      setIsSubmitted(false); // Reset isSubmitted to false
    }
  }, [isSubmitted]);

  return (
    <div className="SignUp">
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <br />
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <br />
        <button type="submit">Sign Up</button>
      </form>
      {message && <p>{typeof message === 'object' ? JSON.stringify(message) : message}</p>}
    </div>
  );
}

export default SignUp;
